import React, { useState, useEffect } from "react";
import axios from "axios";
import FilterForm from "./components/FilterForm";
import ItemList from "./components/ItemList";
import Pagination from "./components/Pagination";

const App = () => {
  const [filters, setFilters] = useState({
    category: "",
    minPrice: "",
    maxPrice: "",
    size: "",
    color: "",
    designer: "",
    rating: "",
  });
  const [sortBy, setSortBy] = useState(""); // Sorting by price or rating
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 5, // Number of items per page
  });
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch items with applied filters, sorting, and pagination
  const fetchItems = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:3001/api/items", {
        params: { ...filters, sortBy, ...pagination },
      });
      setItems(response.data);
    } catch (error) {
      setError("An error occurred while fetching the items.");
    } finally {
      setLoading(false);
    }
  };

  // Trigger fetch when filters, sortBy, or pagination change
  useEffect(() => {
    fetchItems();
  }, [filters, sortBy, pagination]);

  // Handle filter changes from the form
  const handleFilterChange = (newFilters) => {
    setFilters({ ...filters, ...newFilters });
  };

  // Handle sorting change
  const handleSortChange = (event) => {
    setSortBy(event.target.value);
  };

  // Handle pagination change
  const handlePageChange = (page) => {
    setPagination((prevPagination) => ({ ...prevPagination, page }));
  };

  return (
    <div className="app max-w-screen-lg mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-6">Fashion Items</h1>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
        {/* Filter Form */}
        <div className="col-span-1">
          <FilterForm filters={filters} onFilterChange={handleFilterChange} />
        </div>

        {/* Sorting */}
        <div className="col-span-1 md:col-span-3">
          <div className="flex justify-between items-center">
            <label htmlFor="sortBy" className="text-lg">Sort by:</label>
            <select
              id="sortBy"
              onChange={handleSortChange}
              value={sortBy}
              className="px-3 py-2 border border-gray-300 rounded-md"
            >
              <option value="">Select</option>
              <option value="price">Price</option>
              <option value="rating">Rating</option>
            </select>
          </div>
        </div>
      </div>

      {/* Display loading or error message */}
      {loading && <p className="text-center text-lg">Loading items...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}

      {/* Item List */}
      <ItemList items={items.items} />

      {/* Pagination */}
      <Pagination
        currentPage={items.page}
        totalPages={items.totalPages}
        onPageChange={handlePageChange}
      />
    </div>
  );
};

export default App;
