import React from "react";

const ItemList = ({ items }) => {
  // Helper function to display stars based on rating
  const renderStars = (rating) => {
    let stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span
          key={i}
          className={`${
            i <= rating ? "text-yellow-500" : "text-gray-300"
          } font-bold`}
        >
          â˜…
        </span>
      );
    }
    return stars;
  };

  // Helper function to format price as currency
  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
    }).format(price);
  };

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
      {items.map((item) => (
        <div
          key={item.id}
          className="border p-4 rounded-lg shadow-lg transition-transform transform hover:scale-105"
        >
          <img
            src={`https://via.placeholder.com/150?text=${item.category}`}
            alt={item.category}
            className="w-full h-40 object-cover rounded-t-lg"
          />
          <h3 className="text-lg font-semibold mt-2">{item.designer}</h3>
          <p className="text-sm text-gray-500">{item.category}</p>
          <p className="text-xl font-bold text-gray-800">{formatPrice(item.price)}</p>
          <div className="flex items-center mt-1">
            <div className="flex">{renderStars(item.rating)}</div>
            <span className="ml-2 text-sm text-gray-600">({item.rating})</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ItemList;
