import React, { useState, useEffect } from "react";
import axios from "axios";
import FilterForm from "./components/FilterForm";
import ItemList from "./components/ItemList";
import Pagination from "./components/Pagination";

const App = () => {
  const [sortBy, setSortBy] = useState(""); // Sorting by price or rating
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 5, // Number of items per page
  });
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [filters, setFilters] = useState({
    category: "",
    minPrice: "",
    maxPrice: "",
    size: "",
    color: "",
    designer: "",
    rating: "",
  });

  const fetchItems = async (filters) => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:3001/api/items", {
        params: { ...filters, sortBy, ...pagination },
      });
      setItems(response.data); // Assuming API returns data in { items, page, totalPages }
    } catch (error) {
      setError("An error occurred while fetching the items.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchItems(filters); // Fetch on initial load or filter change
  }, [filters, sortBy, pagination]);

  const handleSortChange = (event) => {
    setSortBy(event.target.value);
  };

  const handlePageChange = (page) => {
    setPagination((prevPagination) => ({ ...prevPagination, page }));
  };

  // Function to apply filters when the button is clicked
  const applyFilters = () => {
    fetchItems(filters); // Trigger filter fetch when Apply Filters is clicked
  };

  return (
    <div className="app">
      <h1>Fashion Items</h1>

      {/* Filter Form */}
      <FilterForm filters={filters} setFilters={setFilters} />

      {/* Apply Filters Button */}
      <button onClick={applyFilters}>Apply Filters</button>

      {/* Sorting */}
      <div>
        <label>Sort by:</label>
        <select onChange={handleSortChange} value={sortBy}>
          <option value="">Select</option>
          <option value="price">Price</option>
          <option value="rating">Rating</option>
        </select>
      </div>

      {/* Display loading or error message */}
      {loading && <p>Loading items...</p>}
      {error && <p>{error}</p>}

      {/* Item List */}
      <ItemList items={items.items || []} />

      {/* Pagination */}
      <Pagination
        currentPage={items.page || 1}
        totalPages={items.totalPages || 1}
        onPageChange={handlePageChange}
      />
    </div>
  );
};

export default App;
