import React, { useState, useEffect } from "react";
import axios from "axios";
import FilterForm from "./components/FilterForm";
import ItemList from "./components/ItemList";
import Pagination from "./components/Pagination";

const App = () => {
  const [sortBy, setSortBy] = useState("price");   // Default sort by price
  const [sortOrder, setSortOrder] = useState("asc"); // Default order: ascending
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 8, // Number of items per page
  });
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Function to fetch items based on filters
  const fetchItems = async (filters) => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:3001/api/items", {
        params: { ...filters, sortBy, sortOrder, ...pagination },
      });
      setItems(response.data);  // Assuming API returns data in { items, page, totalPages } format
    } catch (error) {
      setError("An error occurred while fetching the items.");
    } finally {
      setLoading(false);
    }
  };

  // Trigger fetch when sortBy, sortOrder, or pagination change
  useEffect(() => {
    fetchItems({}); // Passing empty object for initial load
  }, [sortBy, sortOrder, pagination]);

  // Handle filter changes from the form
  const handleFilterChange = (newFilters) => {
    fetchItems(newFilters); // Fetching items based on updated filters
  };

  // Handle sort change (both field and order)
  const handleSortChange = (event) => {
    const [field, order] = event.target.value.split("-"); // Value like "price-asc"
    setSortBy(field);    // Set the sort field (price or rating)
    setSortOrder(order); // Set the sort order (asc or desc)
  };

  // Handle page change
  const handlePageChange = (page) => {
    setPagination((prevPagination) => ({ ...prevPagination, page }));
  };

  return (
    <div className="app">
      <h1 className="text-3xl font-semibold text-center my-4">Fashion Store</h1>

      {/* Filter Form */}
      <FilterForm onFilterChange={handleFilterChange} />

      {/* Sorting */}
      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700">Sort by:</label>
        <select
          onChange={handleSortChange}
          value={`${sortBy}-${sortOrder}`} // Value is now a combination of field and order
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm p-3 focus:outline-none focus:ring-2 focus:ring-blue-400"
        >
          <option value="price-asc">Price: Low to High</option>
          <option value="price-desc">Price: High to Low</option>
          <option value="rating-asc">Rating: Low to High</option>
          <option value="rating-desc">Rating: High to Low</option>
        </select>
      </div>

      {/* Display loading or error message */}
      {loading && <p>Loading items...</p>}
      {error && <p>{error}</p>}

      {/* Item List */}
      <ItemList items={items.items || []} />

      {/* Pagination */}
      <Pagination
        currentPage={items.page || 1}
        totalPages={items.totalPages || 1}
        onPageChange={handlePageChange}
      />
    </div>
  );
};

export default App;
